ModuleMappings.DefaultScss:
$gray1: #f0f2f4;
$gray2: #313a47;
$gray3: #8896a1;
$gray4: #313a3c;
$primary-color: var(--primary);

.module-product-details {
  padding: 2rem 0;

  &__container{
    margin-left: 0.5rem;
  }
}

.product-images {
  border: 1px solid $gray1;
  padding: 20px 50px;
  height: 100%;
  @include media-breakpoint-up(sm) {
    max-width: calc(50% - 1rem);
  }

  &-container {
    margin: auto;

    @include media-breakpoint-up(sm) {
      max-width: 250px;
    }

    @include media-breakpoint-up(md) {
      max-width: 350px;
    }
  }

  &-main {
    height: 100%;
    width: 100%;
    max-height: 500px;
    margin: 0;

    &-image {
      display: flex;
      position: relative;

      &::after {
        display: block;
        content: '';
        padding-bottom: 100%;
      }
    }
  }

  &-thumbnails {
    display: flex;

    &-thumbnail {
      display: none;
      padding: 0;
      margin: 0 3px;
      position: relative;
      border: 1px solid transparent;
      max-width: 30%;

      &::after {
        display: block;
        content: '';
        padding-bottom: 100%;
      }

      &.active {
        display: flex;
      }

      img {
        cursor: pointer;
      }

      &.selected {
        border: 1px solid $primary-color;
        border-radius: 3px;
      }
    }
  }

  img {
    width: 100%;
    height: 100%;
    object-fit: contain;
    display: block;
    position: absolute;
  }
}

.lp-param-photoGallery {
  margin-top: 20px;
  justify-content: space-evenly;
}

.lp-param-name {
  font-size: 32px;
  color: $gray4;
}

.lp-product-right-column {
  margin-left: 0.5rem;
}

.lp-product-summary {
  padding-bottom: 4px;
  display: flex;
  white-space: nowrap;
  margin-bottom: 0.25rem;

  &-size, &-color {
    font-size: 21px;
    color: $gray2;
    font-weight: bold;
  }

  &-stockStatus {
    font-size: 21px;
    color: $gray2;
    margin-left: 15px;
  }

  &-divider {
    border-left: 3px solid $gray2;
    margin: 5px;
  }
}

.lp-param-description {
  font-size: 16px;
  margin: 0.5rem 0;

  p {
    margin-block-end: 0;
    margin-block-start: 0;
  }
}

.lp-param-price {
  color: $primary-color;
  font-size: 32px;
  margin-bottom: 0.5rem;

  &.mobile-view {
    font-size: 21px;
  }
}

.lp-param-buttons {
  display: flex;
  justify-content: center;
  flex-direction: column;

  @include media-breakpoint-up(sm) {
    justify-content: normal;
    flex-direction: unset;
  }
}

.btn {
  font-size: 16px;
  width: 100%;
  margin: 10px 0;
  color: white;

  &:not(:last-child) {
    @include media-breakpoint-up(sm) {
      margin-right: 15px;
    }
  }

  &:last-child {
    background-color: $gray4;
    border-color: $gray4;
  }

  @include media-breakpoint-up(sm) {
    width: auto;
    margin: 0;
  }
}

@mixin large-arrow {
  border-bottom: 15px solid transparent;
  border-top: 15px solid transparent;
}

@mixin small-arrow {
  border-bottom: 10px solid transparent;
  border-top: 10px solid transparent;
}

@mixin large-left-arrow {
  border-right: 10px solid $gray3;
}

@mixin small-left-arrow {
  border-right: 5px solid $gray3;
}

@mixin large-right-arrow {
  border-left: 10px solid $gray3;
}

@mixin small-right-arrow {
  border-left: 5px solid $gray3;
}

.lp-arrow {
  width: 0;
  height: 0;
  opacity: 0.5;
  @include large-arrow;

  @include media-breakpoint-up(sm) {
    @include small-arrow;
  }
  @include media-breakpoint-up(md) {
    @include large-arrow;
  }

  &.arrow-left {
    @include large-left-arrow;

    @include media-breakpoint-up(sm) {
      @include small-left-arrow;
    }
    @include media-breakpoint-up(md) {
      @include large-left-arrow;
    }
  }

  &.arrow-right {
    @include large-right-arrow;

    @include media-breakpoint-up(sm) {
      @include small-right-arrow;
    }
    @include media-breakpoint-up(md) {
      @include large-right-arrow;
    }
  }

  &.active {
    opacity: 1;
    cursor: pointer;
  }

  &.hide {
    display: none;
  }
}

.mobile-view {
  display: flex;
  @include media-breakpoint-up(sm) {
    display: none;
  }
}

.desktop-view {
  display: none;
  @include media-breakpoint-up(sm) {
    display: flex;
  }
}

